################################################################################

nbAvecNumero = 0
nbContenantAude = 0
nbCommencantParMaj = 0
nbAvecMajuscule = 0
nbUnderscore = 0

journalists.each do |journalist|
    if journalist =~ /\d/ # regex 'au moins un chiffre'
        nbAvecNumero += 1
    end
    
    if journalist.downcase.include? "aude"
        nbContenantAude += 1
    end
    
    if journalist =~ /^@[A-Z]/ # regex '@ suivi d'une majuscule'
        nbCommencantParMaj += 1
    end
    
    if journalist =~ /[A-Z]/ # regex 'au moins une majuscule'
        nbAvecMajuscule += 1
    end
    
    nbUnderscore += journalist.scan(/_/).count
end

################################################################################
choice = -1

while choice != 9
    choice = -1 # necessaire pour REvenir dans le while suivant 
    while ! (choice >= 0 && choice <= 9)
        puts "
0 - Combien y a-t-il de journalistes dans ce array ?
1 - Combien d'handle contiennent un numéro ?
2 - Combien d'handle contiennent les 4 lettres du prénom 'Aude' à la suite (sans prendre en compte les majuscules) ?
3 - Combien commencent par une majuscule (première lettre juste après le @) ?
4 - Combien contiennent au moins une majuscule ?
5 - Combien y a-t-il de underscore _ dans tous les pseudos confondus ?
6 - Trie la liste de handle par ordre alphabétique.
7 - Quels sont les 50 handles les plus courts de ce array ?
8 - Quelle est la position dans l'array de la personne @epenser ?
9 - Quitter"
        print "> "
        choice = gets.chomp.to_i
    end

    if choice == 0
        puts "il y a #{journalists.size} journaliste(s)"
    elsif choice == 1
        puts "il y a #{nbAvecNumero} d'handle contenant un numéro"
    elsif choice == 2
        puts "il y a #{nbContenantAude} d'handle contenant 'aude' (sans prendre en compte les majuscules)"
    elsif choice == 3
        puts "il y a #{nbCommencantParMaj} d'handle commençant par une majuscule"
    elsif choice == 4
        puts "il y a #{nbAvecMajuscule} d'handle contenant au moins une majuscule"
    elsif choice == 5
        puts "il y a #{nbUnderscore} underscore(s) dans tous les pseudos confondus"
    elsif choice == 6
        journalists = journalists.sort
        puts "la liste de handle est triée par ordre alphabétique : "
        journalists.each {|pseudo| puts pseudo}
    elsif choice == 7
        temp = journalists.sort_by{|pseudo| pseudo.length}
        puts "Voici les 50 hadles les plus courts :"
        50.times do |index|
            puts temp[index]
        end
    elsif choice == 8
        position = journalists.index("@epenser")
        puts "@epenser est à la position #{position}"
    else
        puts "Au revoir !"
    end
end

